from random import random

print("В данной программе нужно угадать случайное целое число от 0 до 100.\nНа отгадку дается 10 попыток.")

n = round(random() * 100)
i = 1
while i <= 10:
    a = int(input(str(i) + '-я попытка: '))
    if a > n:
        print('Загаданно число меньше, чем Ваш ответ.')
    elif a < n:
        print('Загаданно число больше, чем Ваш ответ.')
    else:
        print('Это правильный ответ.')
        break
    i += 1
else:
    print('Попытки закончились, правильный ответ: ', n)

